SELECT PROCESS_ID, DOC_ROOT_ID, NAME, STATUS, CREATOR_ID, CREATOR_NAME, EXPECT_DATE, COMPLETE_DATE FROM ( SELECT DISTINCT P.PROCESS_ID, P.DOC_ROOT_ID, P.NAME, P.STATUS, P.CREATOR_ID, P.CREATOR_NAME, P.EXPECT_DATE, P.COMPLETE_DATE, CEIL((ROW_NUMBER() OVER(ORDER BY ${orderCol} ${orderType} )) / ${page_size} ) PAGENUM FROM XR_PROCESS P INNER JOIN XR_PROCESS_EXECUTOR PE ON PE.EXECUTOR_ID = #{user_id} AND PE.PROCESS_ID = P.PROCESS_ID
AND (PE.TYPE = 'R' OR PE.TYPE = 'C') AND PE.STATUS = 'S' AND PE.TYPE = 'A' AND PE.STATUS = 'S' AND PE.TYPE = 'Q' AND (PE.TYPE = 'R' OR PE.TYPE = 'C') AND PE.STATUS = 'E' AND PE.TYPE = 'A' AND PE.STATUS = 'E' AND PE.TYPE = 'I' AND PE.STATUS = 'E' AND (PE.TYPE = 'R' OR PE.TYPE = 'C') AND PE.STATUS = 'S'
P.STATUS != 'E' P.STATUS IN ('W', 'M') P.STATUS = 'A' P.STATUS = 'M' P.STATUS = 'E'
AND ${strIndexColumn} LIKE '%${strKeyword1}%' AND P.CREATE_DATE >= TO_DATE(#{sdate},'YYYY-MM-DD') AND P.CREATE_DATE < TO_DATE(#{edate},'YYYY-MM-DD')+0.99999
) LIST WHERE LIST.PAGENUM = #{nPage}

SELECT PROCESS_ID, DOC_ROOT_ID, CREATOR_ID, CREATOR_NAME, CREATE_DATE,
	   NAME, STATUS, EXPECT_DATE, COMPLETE_DATE, CONTENT
 FROM XR_PROCESS

	
		
			AND DOC_ROOT_ID = #{doc_root_id}
		
		
			AND PROCESS_ID = #{process_id}

SELECT FOLDER_ID
 FROM XR_LINKED
WHERE DOC_ID = #{doc_id}

SELECT EXECUTE_ID, STATUS
FROM XR_PROCESS_EXECUTOR
WHERE PROCESS_ID = #{process_id} AND EXECUTOR_ID = #{executor_id} AND TYPE = #{type}
